mapper.select:
select
from resource_usage u
where u.tspd_id = #{tspdId} and merge_id = -1 and usage_status < 500 and time_begin < #{timeEnd} and time_end > #{timeBegin}

select

from resource_usage u
WHERE tspd_id = #{usage.tspdId}

    and task_id != #{taskId}


    and net_no != #{netNo}


    and usage_status < 500
    and down_freq_begin < #{usage.freqEnd} and down_freq_end > #{usage.freqBegin}
    and time_begin < #{usage.timeEnd} and time_end > #{usage.timeBegin}

limit 1

mapper.update:
update resource_usage set merge_id = #{mergeId}
where id in

    #{id}
